mapper.select:

select 

from check_assignment t
where t.id = #{id,jdbcType=BIGINT}

select 

from check_assignment t
where t.project_id = #{projectId,jdbcType=BIGINT}
  and t.task_id = #{taskId,jdbcType=BIGINT}
  and t.round_id = #{roundId,jdbcType=BIGINT}
  and t.user_id = #{userId,jdbcType=BIGINT}

    and t.id != #{notThisId,jdbcType=BIGINT}

select

from check_assignment t
where 1=1

  and t.project_id = #{projectId,jdbcType=BIGINT}


  and t.task_id = #{taskId,jdbcType=BIGINT}


  and t.round_id = #{roundId,jdbcType=BIGINT}


  and t.assign_status = #{assignStatus,jdbcType=CHAR}


  and t.user_id = #{userId,jdbcType=BIGINT}


  order by ${orderByClause}

select 
  sum(t.amount) as assignedAmount
from check_assignment t
where t.project_id = #{projectId,jdbcType=BIGINT}
  and t.task_id = #{taskId,jdbcType=BIGINT}
  and t.round_id = #{roundId,jdbcType=BIGINT}
