mapper.select:

select 

from ct_common t
where t.id = #{id,jdbcType=BIGINT}

select

from ct_common t
where t.country_code = #{countryCode,jdbcType=VARCHAR}
  and t.language_code = #{languageCode,jdbcType=VARCHAR}

  and t.original_type = #{originalType,jdbcType=CHAR}


  and (t.original like #{keyWord,jdbcType=VARCHAR}
    or t.original_abbr like #{keyWord,jdbcType=VARCHAR}
    or t.roman like #{keyWord,jdbcType=VARCHAR}
    or t.transliteration like #{keyWord,jdbcType=VARCHAR}
    or t.free_translation like #{keyWord,jdbcType=VARCHAR}
  )


  order by ${orderByClause}

mapper.delete:
delete from ct_common
where country_code = #{countryCode,jdbcType=VARCHAR}
  and language_code = #{languageCode,jdbcType=VARCHAR}
