mapper.select:

select 

from ct_roman t
where t.id = #{id,jdbcType=BIGINT}

mapper.update:

update ct_roman
set country_code = #{countryCode,jdbcType=VARCHAR},
  language_code = #{languageCode,jdbcType=VARCHAR},
  original_alpha = #{originalAlpha,jdbcType=VARCHAR},
  roman_alpha = #{romanAlpha,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select

from ct_roman t
where t.country_code = #{countryCode,jdbcType=VARCHAR}
  and t.language_code = #{languageCode,jdbcType=VARCHAR}

  order by ${orderByClause}

mapper.delete:
delete from ct_roman
where country_code = #{countryCode,jdbcType=VARCHAR}
  and language_code = #{languageCode,jdbcType=VARCHAR}
